SELECT (to_date('2017-01-01') AS base) + INTERVAL number MONTH AS d, to_datetime(d, 'UTC') AS t, toQuarter(d) AS qd, toQuarter(t) AS qt, to_start_of_quarter(d) AS sqd, to_start_of_quarter(t) AS sqt, toRelativeQuarterNum(d) - toRelativeQuarterNum(base) AS qdiff_d, toRelativeQuarterNum(t) - toRelativeQuarterNum(base) as qdiff_t FROM system.numbers LIMIT 24;
